<script setup lang="ts">
import { ElMessage } from "element-plus";
import { ref, reactive } from "vue";
import { useUserStore } from "@/store/modules/user";

const userConfig = useRuntimeConfig();
const {
  VITE_API_HOST,
  VITE_API_PREFFIX,
  VITE_PACK_ENV,
  VITE_PACK_URL,
  VITE_USER_ID,
} = userConfig.public;
console.log(userConfig);

const loginForm = ref({
  username: "",
  password: "",
  code: "",
});

async function toLogin() {
  if (
    loginForm.value.username === "" ||
    loginForm.value.password === "" ||
    loginForm.value.code === ""
  ) {
    ElMessage.warning("请正确输入信息");
    return;
  }

  if (process.client) {
    const res = await useUserStore().login(loginForm.value);
    console.log(res);
  }
}

const codeUrl = ref(VITE_PACK_URL + "/user/code");
const resetCode = () => {
  codeUrl.value += "?" + new Date().getTime();
};
</script>

